"use strict";var box=$("#box"),nom=$("#ville");$("#selec").click(function(){$.ajax({url:"http://api.openweathermap.org/data/2.5/weather?q="+nom.val()+",fr&appid=2304ca0a4c5b96eca86deb93757adbfa",type:"GET",dataType:"json"}).done(function(e){box.html("Ville: "+e.name+"</br>longitude: "+e.coord.lon+"</br>lattitude: "+e.coord.lat+"</br>vent: "+e.wind.speed+" Km/h </br>Taux d'humidité: "+e.main.humidity+"</br>Pression Atmosphérique: "+e.main.pressure+"</br>Température: "+(e.main.temp-273.15).toFixed(2)+" °C</br>Température Min: "+(e.main.temp_min-273.15).toFixed(2)+" °C</br>Température Max: "+(e.main.temp_max-273.15).toFixed(2)+" °C</br>")}).fail(function(e){console.log(e)}).always(function(){console.log("complete"),console.log(nom.val())})});